import { useContext, useReducer } from "react";
import { createContext } from "react";

const homeContext = createContext({});

const initState = {
  count: 5,
  showPanel: false,
  movies: [],
};

const reducer = (state, action) => {
  switch (action.type) {
    case "SET_COUNT":
      return { ...state, count: action.payload };
    default:
      return state;
  }
};

export const HomeProvider = ({ children }) => {
  const { globalState, globalDispatch } = useReducer(initState, reducer);

  return (
    <homeContext.HomeProvider value={(globalState, globalDispatch)}>
      {children}
    </homeContext.HomeProvider>
  );
};

export const useHomeProvider = () => {
  const homeData = useContext(homeContext);
  return homeData;
};
