import { useContext, useReducer, createContext } from "react";

const homeProvider = createContext({});

const initState = {
  count: 5,
  showPanel: false,
  movies: [],
};

const reducer = (state, action) => {
  switch (action.type) {
    case "SET_COUNT":
      return { ...state, count: action.payload };
    default:
      return state;
  }
};

export const HomeProvider = ({ children }) => {
  const [globalState, globalDispatch] = useReducer(initState, reducer);

  return (
    <homeProvider.HomeProvider value={globalState, globalDispatch }>
      {children}
    </homeProvider.HomeProvider>
  );
};

export const useHomeProvider = () => {
  const homeProviderData = useContext(homeProvider);
  return homeProviderData;
};
